create table tb_cliente (id int8 generated by default as identity, cliente_cep varchar(255), cliente_cnpj varchar(255), cliente_cpf varchar(255), cliente_email varchar(255), cliente_endereco varchar(255), cliente_estado varchar(255), cliente_fone varchar(255), cliente_municipio varchar(255), cliente_pais varchar(255), cliente_senha varchar(255), cliente_usuario varchar(255), nome varchar(255), primary key (id));
create table tb_grupo (id int8 generated by default as identity, gruponome varchar(255), primary key (id));
create table tb_grupo_produto (grupo_id int8 not null, produto_id int8 not null, primary key (grupo_id, produto_id));
create table tb_pedido (id int8 generated by default as identity, pedidomomento timestamp, pedidostatus int4, pedidototal float8, primary key (id));
create table tb_pedido_cliente (pedido_id int8 not null, cliente_id int8 not null, primary key (pedido_id, cliente_id));
create table tb_pedido_produto (pedido_id int8 not null, produto_id int8 not null, primary key (pedido_id, produto_id));
create table tb_produto (id int8 generated by default as identity, produto_descricao varchar(255), produto_imagemuri_1 varchar(255), produto_imagemuri_2 varchar(255), produto_imagemuri_3 varchar(255), produto_imagemuri_4 varchar(255), produto_imagemuri_5 varchar(255), produto_preco float8, produtonome varchar(255), primary key (id));
create table tb_produto_grupo (produto_id int8 not null, grupo_id int8 not null, primary key (produto_id, grupo_id));
